import './App.css';
import React from "react";
import Books from '../Books/books';
import LabService from "../../repository/labRepository";

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            books : [],
        }
    }

    render() {
        return (
            <Books/>
        );
    }

    loadBooks = () =>{
        LabService.fetchBooks()
            .then((data) => {
                this.setState({
                    books: data.data
                });
            })
    }

    componentDidMount() {
        this.loadBooks();
    }

}

export default App;
