import './App.css';
import React from "react";
import Books from '../Books/BookList/books';
import LabService from "../../repository/labRepository";
import {BrowserRouter as Router, Redirect, Route, Routes} from 'react-router-dom'
import Categories from '../Categories/categories'
import Header from '../Header/header'
import BookAdd from '../Books/BookAdd/bookAdd'
import Authors from '../Authors/AuthorList/authors'

class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            books : [],
            categories: [],
            authors: []
        }
    }

    render() {
        return (
            <Router>
                <Header/>
                <main>
                    <div className={"container"}>
                        <Routes>
                            <Route path={"/books/add"} element={<BookAdd authors={this.state.authors} categories={this.state.categories} onAddBook={this.addBook}/>}/>
                            <Route path={"/books"} element={<Books books={this.state.books} onDelete={this.deleteBook}/>}/>
                            <Route path={"/"} element={<Books books={this.state.books}/>}/>
                            <Route path={"/categories"} element={<Categories categories={this.state.categories}/>}/>
                            <Route path={"/authors"} element={<Authors authors={this.state.authors}/>}/>
                            <Redirect to={"/books"}/>
                        </Routes>

                    </div>
                </main>
            </Router>
        );
    }

    componentDidMount() {
        this.loadBooks();
        this.loadCategories();
        this.loadAuthors();
    }

    loadBooks = () =>{
        LabService.fetchBooks()
            .then((data) => {
                this.setState({
                    books: data.data
                });
            })
    }

    loadCategories = () =>{
        LabService.fetchCategories()
            .then((data) => {
                this.setState({
                    categories: data.data
                });
            })
    }

    loadAuthors= () =>{
        LabService.fetchAuthors()
            .then((data) => {
                this.setState({
                    categories: data.data
                });
            })
    }

    deleteBook = (id) =>{
        LabService.deleteBook(id)
            .then(() =>{
                this.loadBooks();
            })
    }

    addBook = (name, author, category, availableCopies) => {
        LabService.addBook(name, author, category, availableCopies)
            .then(() => this.loadBooks());
    }

}

export default App;
