import './App.css';
import React from "react";
import Books from '../Books/books';
import LabService from "../../repository/labRepository";
import {BrowserRouter as Router, Redirect, Route, Routes} from 'react-router-dom'
//import Categories from '../Categories/categories'


class App extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            books : [],
            categories: []
        }
    }

    render() {
        return (
            <Router>
                <Routes>
                    <Route exact path={"/books"}> <Books books={this.state.books}/></Route>
                </Routes>
                <main>
                    <div className={"container"}>






                        {/*<Route path={"/categories"} exact render={() => <Categories categories={this.state.categories}/>}/>*/}

                    </div>
                </main>
            </Router>
        );
    }

    loadBooks = () =>{
        LabService.fetchBooks()
            .then((data) => {
                this.setState({
                    books: data.data
                });
            })
    }

    // loadCategories = () =>{
    //     LabService.fetchCategories()
    //         .then((data) => {
    //             this.setState({
    //                 categories: data.data
    //             });
    //         })
    // }

    componentDidMount() {
        this.loadBooks();
        // this.loadCategories()
    }

}

export default App;
